import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import "../styles/NewsArticle.css";

export default function NewsArticle() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [article, setArticle] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:8080/api/news/${id}`)
      .then((res) => res.json())
      .then((data) => setArticle(data))
      .catch((err) => console.error(err));
  }, [id]);

  if (!article) return <p className="loading">Caricamento...</p>;

  const imageUrl = `http://localhost:8080/images/${article.thumbnail}`;

  return (
    <div className="newsarticle-page fade-in">

      {/* HERO SECTION */}
      <div className="news-hero">
        <img src={imageUrl} alt="thumbnail" className="news-thumb" />

        <div
          className="news-hero-bg"
          style={{ backgroundImage: `url(${imageUrl})` }}
        ></div>

        <div className="news-hero-content">
          <button className="back-btn" onClick={() => navigate(-1)}>
            ‚Üê Torna indietro
          </button>

          <h1 className="news-title">{article.title}</h1>
          <div className="news-meta">Pubblicato: {article.date}</div>
        </div>
      </div>

      {/* BODY */}
      <div className="news-body">
        {article.content.split("\n").map((p, i) => (
          <p key={i}>{p}</p>
        ))}
      </div>
    </div>
  );
}
