import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/newsArticle.css';

export default function NewsArticle() {

  const { id } = useParams();
  const [article, setArticle] = useState(null);

  useEffect(() => {
    fetch("http://localhost:8080/api/news/" + id)
      .then(r => r.json())
      .then(setArticle);
  }, [id]);

  if (!article) return <div className="container">Caricamento...</div>;

  return (
    <div className="container fade-article">

      {/* HERO (sfondo blur + thumb a sinistra) */}
      <div
        className="news-hero"
        style={{ "--bg": `url(http://localhost:8080/api/images/${article.thumbnail})` }}
      >

        <div className="news-hero-content">

          <img
            className="news-thumb"
            src={`http://localhost:8080/api/images/${article.thumbnail}`}
            alt="thumb"
          />

          <div className="news-info">
            <h1 className="news-title">{article.title}</h1>
          </div>

        </div>
      </div>

      {/* TESTO DELLâ€™ARTICOLO */}
      <div className="news-body">
        <p className="news-text">{article.content}</p>
      </div>

    </div>
  );
}
