import React, { useEffect, useState } from 'react';
import '../styles/home.css';
import { useNavigate } from 'react-router-dom';

export default function Home() {
  const [data, setData] = useState({ drammatico: [], sentimentale: [] });
  const [selected, setSelected] = useState('drammatico');
  const navigate = useNavigate();

  useEffect(() => {
    fetch('http://localhost:8080/api/genres')
      .then(r => r.json())
      .then(setData);
  }, []);

  const films = data[selected] || [];

  // ðŸ”¥ ANIMAZIONE QUANDO CAMBIA GENERE
  useEffect(() => {
    const cards = document.querySelectorAll(".film-card");
    cards.forEach(card => {
      card.classList.remove("card-enter");
      void card.offsetWidth;
      card.classList.add("card-enter");
    });
  }, [selected]);

  return (
    <div className='container'>
      <h2 className='section-title'>Genere</h2>

      <div className='genre-tabs'>
        <div
          className={'tab ' + (selected === 'drammatico' ? 'active' : '')}
          onClick={() => setSelected('drammatico')}
        >
          Drammatico
        </div>
        <div
          className={'tab ' + (selected === 'sentimentale' ? 'active' : '')}
          onClick={() => setSelected('sentimentale')}
        >
          Sentimentale
        </div>
      </div>

      <div className='card-grid'>
        {films.slice(0, 3).map(f => (
          <div key={f.id} className="film-card card-enter">
            <img
              className='thumb'
              alt={f.title}
              src={'http://localhost:8080/api/images/' + f.thumbnail}
              onClick={() => navigate('/film/' + f.id)}
            />

            <div className="card-title">{f.title} ({f.year})</div>
          </div>
        ))}
      </div>
    </div>
  );
}
