import React, {useEffect, useState} from 'react';
import '../styles/calendar.css';
/**
 * Calendar page shows release groups. Selecting a period highlights it and shows 2 films.
 */
export default function Calendar(){
  const [releases, setReleases] = useState({});
  const [active, setActive] = useState('this');

  useEffect(()=>{ fetch('http://localhost:8080/api/releases').then(r=>r.json()).then(setReleases); },[]);

  const groups = {previous:'Scorsa settimana', this:'Questa settimana', week1:'Tra 1 settimana', week2:'Tra 2 settimane', week3:'Tra 3 settimane'};

  return (<div className='container'>
    <h2 className='section-title'>Calendario Uscite</h2>
    <div className='calendar-controls'>
      {Object.keys(groups).map(k=>(
        <div key={k} className={'choice '+(active===k?'active':'')} onClick={()=>setActive(k)}>{groups[k]}</div>
      ))}
      <input type='date' onChange={(e)=>{ /* date-based filter could be added here */ }} style={{marginLeft:12}}/>
    </div>
    <div className='card-grid'>
      {(releases[active]||[]).map(f=>(
        <div key={f.id} style={{textAlign:'center'}}>
          <img className='thumb' src={'http://localhost:8080/api/images/'+f.thumbnail} alt={f.title}/>
          <div style={{marginTop:6}}>{f.title}<div style={{color:'#cbd9ea'}}>{f.releaseDate}</div></div>
        </div>
      ))}
    </div>
  </div>);
}
