import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import "../styles/newsArticle.css";

export default function NewsArticle() {
  const { id } = useParams();
  const [article, setArticle] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:8080/api/news/${id}`)
      .then((r) => r.json())
      .then(setArticle)
      .catch(() => setArticle(null));
  }, [id]);

  if (!article) return <div className="article-loading">Caricamento...</div>;

  const imageUrl = `http://localhost:8080/api/images/${article.thumbnail}`;

  return (
    <div className="article-container">
      <div className="article-hero" style={{ backgroundImage: `url(${imageUrl})` }}>
        <div className="hero-overlay" />
        <div className="hero-content">
          <img className="hero-thumb" src={imageUrl} alt={article.title} />
          <div className="hero-info">
            <div className="article-category">News</div>
            <h1 className="article-title">{article.title}</h1>
          </div>
        </div>
      </div>

      <div className="article-body">
        <Link to="/news" className="back-btn">‚Üê Torna alle News</Link>

        <div className="article-content">
          {article.content.split("\n").map((p, i) => (
            <p key={i} className="article-paragraph">{p.trim()}</p>
          ))}
        </div>
      </div>
    </div>
  );
}
