import React, {useEffect, useState} from 'react';
import '../styles/calendar.css';

/**
 * Calendar page: filter by predefined week groups or by a specific date.
 */
export default function Calendar(){
  const [releases, setReleases] = useState({});
  const [active, setActive] = useState('this');
  const [selectedDate, setSelectedDate] = useState(null);
  const [dateFilteredFilms, setDateFilteredFilms] = useState([]);

  useEffect(()=>{
    // Load release groups from backend
    fetch('http://localhost:8080/api/releases')
      .then(r=>r.json())
      .then(setReleases);
  },[]);

  const groups = {
    previous:'Scorsa settimana',
    this:'Questa settimana',
    week1:'Tra 1 settimana',
    week2:'Tra 2 settimane',
    week3:'Tra 3 settimane'
  };

  /**
   * When user selects a date:
   * - highlight date input (deactivate weekly tabs)
   * - show only films matching exact releaseDate
   */
  function handleDateChange(e){
    const date = e.target.value;
    setSelectedDate(date);

    if(date){
      setActive(null); // deselect predefined groups

      // Flatten all films across groups
      const allFilms = Object.values(releases).flat();

      // Filter by exact date
      const filtered = allFilms.filter(f => f.releaseDate === date);

      setDateFilteredFilms(filtered);
    } else {
      setDateFilteredFilms([]);
      setActive('this'); // default if date cleared
    }
  }

  // Determine which list of films to show
  const filmsToShow = selectedDate
    ? dateFilteredFilms
    : (releases[active] || []);

  return (
    <div className='container'>
      <h2 className='section-title'>Calendario Uscite</h2>

      <div className='calendar-controls'>
        {/* Tabs for fixed periods */}
        {Object.keys(groups).map(k=>(
          <div
            key={k}
            className={'choice '+(active===k?'active':'')}
            onClick={()=>{
              setActive(k);
              setSelectedDate(null);
            }}
          >
            {groups[k]}
          </div>
        ))}

        {/* Date picker */}
        <input
          type='date'
          value={selectedDate || ''}
          onChange={handleDateChange}
          className={selectedDate ? 'choice active' : 'choice'}
          style={{marginLeft:12}}
        />
      </div>

      <div className='card-grid'>
        {filmsToShow.length === 0 ? (
          <div style={{color:'#cbd9ea', marginTop:20}}>
            Nessun film disponibile per la data selezionata.
          </div>
        ) : (
          filmsToShow.map(f=>(
            <div key={f.id} style={{textAlign:'center'}}>
              <img className='thumb'
                src={'http://localhost:8080/api/images/'+f.thumbnail}
                alt={f.title}
              />
              <div style={{marginTop:6}}>
                {f.title}
                <div style={{color:'#cbd9ea'}}>{f.releaseDate}</div>
              </div>
            </div>
          ))
        )}
      </div>
    </div>
  );
}
