import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "../styles/newsArticle.css";

export default function NewsArticle() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [article, setArticle] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    let active = true;

    fetch(`http://localhost:8080/api/news/${id}`)
      .then((r) => r.json())
      .then((data) => active && setArticle(data))
      .catch((e) => console.error("Errore articolo:", e))
      .finally(() => active && setLoading(false));

    return () => (active = false);
  }, [id]);

  if (loading) return <div className="loading">Caricamento...</div>;
  if (!article) return <div className="loading">Articolo non trovato.</div>;

  const img = `http://localhost:8080/api/images/${article.thumbnail}`;

  return (
    <div className="article-container">

      <button className="back-btn" onClick={() => navigate(-1)}>
        ‚Üê Indietro
      </button>

      <img
        src={img}
        className="article-hero"
        alt={article.title}
        onError={(e) => {
          e.currentTarget.onerror = null;
          e.currentTarget.src = "/fallback-news.jpg";
        }}
      />

      <div className="article-meta">MOVIE NEWS</div>

      <h1 className="article-title">{article.title}</h1>

      <div className="article-content">{article.content}</div>
    </div>
  );
}
