import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import "../styles/NewsArticle.css";

function NewsArticle() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [article, setArticle] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:8080/api/news/${id}`)
      .then((res) => res.json())
      .then((data) => setArticle(data))
      .catch((err) => console.error(err));
  }, [id]);

  if (!article) return <p className="loading">Caricamento...</p>;

  return (
    <div className="article-container netflix-article-enter">

      <button className="back-button" onClick={() => navigate(-1)}>
        ‚Üê Torna indietro
      </button>

      <h1 className="article-title">{article.title}</h1>

      <div className="article-image-wrapper">
        <img
          src={article.imageUrl || "/fallback.jpg"}
          alt={article.title}
          className="article-image"
        />
      </div>

      <p className="article-content">{article.content}</p>

      <p className="article-date">
        Pubblicato il: <span>{article.date}</span>
      </p>
    </div>
  );
}

export default NewsArticle;
