import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import "./NewsArticle.css";

export default function NewsArticle() {
    const { id } = useParams();
    const [news, setNews] = useState(null);

    useEffect(() => {
        async function fetchNews() {
            const response = await fetch(`http://localhost:8080/news/${id}`);
            const data = await response.json();
            setNews(data);
        }
        fetchNews();
    }, [id]);

    if (!news) return <div className="loading">Caricamento...</div>;

    return (
        <div className="news-article-container">

            {/* HERO */}
            <div className="news-article-hero">
                <div className="hero-overlay"></div>

                <img
                    src={news.thumbnail}
                    alt=""
                    className="news-article-hero-img"
                />

                <div className="hero-content">
                    <h1 className="news-article-title">{news.title}</h1>

                    <div className="news-article-meta">
                        <span className="category">{news.category?.toUpperCase()}</span>
                        <span className="dot">•</span>
                        <span className="date">{news.date}</span>
                    </div>
                </div>
            </div>

            {/* BODY */}
            <div className="news-article-body">
                <Link to="/news" className="news-article-back">
                    ← Torna alle News
                </Link>

                <div className="news-article-content">
                    <p>{news.content}</p>
                </div>
            </div>
        </div>
    );
}
