import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "../styles/newsArticle.css";

export default function NewsArticle() {
  const { id } = useParams();
  const [article, setArticle] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetch(`http://localhost:8080/api/news/${id}`)
      .then((r) => r.json())
      .then((data) => setArticle(data))
      .catch((err) => {
        console.error("Errore nel caricamento dell'articolo:", err);
        setArticle(null);
      })
      .finally(() => setLoading(false));
  }, [id]);

  if (loading) return <div className="news-loading">Caricamento...</div>;
  if (!article) return <div className="news-loading">Articolo non trovato.</div>;

  const imageUrl = `http://localhost:8080/api/images/${article.thumbnail}`;

  return (
    <div className="news-article-container">

      {/* TITOLO GRANDISSIMO + CATEGORIA */}
      <div className="news-header">
        <div className="news-category">MOVIE NEWS</div>
        <h1 className="news-title">{article.title}</h1>
      </div>

      {/* IMMAGINE GRANDE */}
      <div className="news-hero-wrapper">
        <img
          className="news-hero"
          src={imageUrl}
          alt={article.title}
          onError={(e) => {
            e.currentTarget.onerror = null;
            e.currentTarget.src = "/fallback-news.jpg";
          }}
        />
      </div>

      {/* CONTENUTO */}
      <div className="news-body">
        {article.content?.split("\n").map((p, index) => (
          <p key={index} className="news-paragraph">
            {p}
          </p>
        ))}
      </div>
    </div>
  );
}
