import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/news.css';

export default function NewsArticle() {
  const { id } = useParams();
  const [article, setArticle] = useState(null);
  const [loading, setLoading] = useState(true);

  // Parallax offset
  const [offsetY, setOffsetY] = useState(0);

  useEffect(() => {
    function handleScroll() {
      setOffsetY(window.scrollY * 0.35);
    }
    window.addEventListener('scroll', handleScroll);

    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  useEffect(() => {
    async function load() {
      try {
        const res = await fetch('http://localhost:8080/api/news/' + id);
        const data = await res.json();

        // Se l’articolo non esiste (caso raro), mostra un placeholder
        if (!data) {
          setArticle({
            id,
            title: "Articolo non trovato",
            thumbnail: "news1_thumbnail.jpg",
            content: "Nessun contenuto disponibile."
          });
        } else {
          setArticle(data);
        }
      } catch (e) {
        console.error("Errore caricamento articolo:", e);
      }

      setLoading(false);
    }
    load();
  }, [id]);

  if (loading || !article) {
    return (
      <div className="container fade-news-page">
        <h2 className="section-title">News</h2>
        <div className="loading">Caricamento...</div>
      </div>
    );
  }

  return (
    <div className="container animate-article news-article">

      {/* HERO / PARALLAX */}
      <div
        className="article-hero"
        style={{
          backgroundImage: `url(http://localhost:8080/api/images/${article.thumbnail})`,
          backgroundSize: "cover",
          backgroundPosition: "center",
          transform: `translateY(${offsetY}px)`
        }}
      ></div>

      <h2 className="article-title">{article.title}</h2>

      <div className="article-content">
        {article.content}
        <br /><br />
        {article.content}
        <br /><br />
        {article.content}
      </div>

    </div>
  );
}
