import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/news.css';

export default function NewsArticle() {

  const { id } = useParams();
  const [article, setArticle] = useState(null);

  useEffect(() => {
    window.scrollTo(0, 0);

    async function loadArticle() {
      try {
        // 1️⃣ Prima provo a prendere tutte le news
        const res = await fetch('http://localhost:8080/api/news');
        const list = await res.json();

        // 2️⃣ Cerco quella cliccata
        const found = list.find(x => String(x.id) === id);

        if (found) {
          setArticle(found);
        } else {
          // 3️⃣ Se non c’è → fetch singolo articolo
          const r2 = await fetch(`http://localhost:8080/api/news/${id}`);
          const single = await r2.json();
          setArticle(single);
        }

      } catch (err) {
        console.error("Errore nel caricamento:", err);
      }
    }

    loadArticle();
  }, [id]);

  if (!article)
    return <div className="container fade-news-page">Caricamento...</div>;

  return (
    <div className="container fade-news-page">

      <div className="article-hero-wrapper">
        <img
          src={'http://localhost:8080/api/images/' + article.thumbnail}
          alt={article.title}
          className="article-hero"
        />
      </div>

      <h2 className="article-title">{article.title}</h2>

      <div className="article-content animate-article">
        {article.content.repeat(8)}
      </div>

    </div>
  );
}
