import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "../styles/news.css";

export default function NewsArticle() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [article, setArticle] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    let mounted = true;
    fetch("http://localhost:8080/api/news/" + id)
      .then((r) => r.json())
      .then((data) => {
        if (!mounted) return;
        setArticle(data);
      })
      .catch((e) => console.error("Errore caricamento articolo:", e))
      .finally(() => {
        if (mounted) setLoading(false);
      });
    return () => (mounted = false);
  }, [id]);

  if (loading) {
    return (
      <div className="news-article">
        <p>Caricamento articolo...</p>
      </div>
    );
  }

  if (!article) {
    return (
      <div className="news-article">
        <p>Articolo non trovato</p>
      </div>
    );
  }

  const imageUrl = `http://localhost:8080/api/images/${article.thumbnail}`;

  return (
    <div className="news-article">
      {/* hero: immagine + titolo sovrapposto */}
      <div className="article-hero-wrap">
        <img
          className="article-hero"
          src={imageUrl}
          alt={article.title}
          onError={(ev) => {
            ev.currentTarget.onerror = null;
            ev.currentTarget.src = "/fallback-news.jpg";
          }}
        />

        {/* sovrapposizione per titolo (posizionata sopra l'immagine) */}
        <div className="article-hero-overlay">
          <div className="article-hero-title">{article.title}</div>
        </div>
      </div>

      {/* contenuto: card vetro chiaro sopra sfondo (ma senza full-screen blur) */}
      <div className="article-content-light">
        {article.content.split("\n").map((p, i) => (
          <p key={i}>{p.trim()}</p>
        ))}

        <div className="article-actions">
          <button className="btn" onClick={() => navigate(-1)}>
            ‚Üê Torna indietro
          </button>
        </div>
      </div>
    </div>
  );
}
