import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/news.css';

export default function NewsArticle() {
  const { id } = useParams();
  const [article, setArticle] = useState(null);

  useEffect(() => {
    fetch('http://localhost:8080/api/news')
      .then(r => r.json())
      .then(list => {
        const found = list.find(x => String(x.id) === id);
        if (found) setArticle(found);
        else {
          setArticle({
            id: id,
            title: 'Articolo',
            thumbnail: 'news1_thumbnail.jpg',
            content: 'Contenuto articolo esteso...'
          });
        }
      });
  }, [id]);

  if (!article)
    return <div className="container">Caricamento...</div>;

  return (
    <div className="container news-article animate-article">

      {/* IMMAGINE DI COPERTINA */}
      <img
        className="article-hero"
        src={'http://localhost:8080/api/images/' + article.thumbnail}
        alt={article.title}
      />

      {/* TITOLO */}
      <h1 className="article-title">{article.title}</h1>

      {/* CONTENUTO */}
      <div className="article-content">
        {article.content.repeat(10)}
      </div>

    </div>
  );
}
