import React, {useEffect, useState} from 'react';
import '../styles/news.css';
import { useNavigate } from 'react-router-dom';

/**
 * News page - loads 3 random news from backend.
 * Clicking a news item navigates to /news/:id for full article.
 */
export default function News(){
  const [articles, setArticles] = useState([]);
  const navigate = useNavigate();

  useEffect(()=>{ fetch('http://localhost:8080/api/news').then(r=>r.json()).then(setArticles); },[]);

  return (<div className='container'>
    <h2 className='section-title'>News</h2>
    <div>
      {articles.map(a=>(
        <div className='news-item' key={a.id}>
          <img src={'http://localhost:8080/api/images/'+a.thumbnail} alt={a.title} onClick={()=>navigate('/news/'+a.id)}/>
          <div>
            <h4 style={{margin:'0 0 6px 0',color:'#fbe2a6'}}>News</h4>
            <div style={{fontWeight:'bold'}}>{a.title}</div>
            <p style={{margin:6,color:'#cbd9ea'}}>{a.content.substring(0,160)}...</p>
          </div>
        </div>
      ))}
    </div>
  </div>);
}
