import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/news.css';

export default function NewsArticle() {

  const { id } = useParams();
  const [article, setArticle] = useState(null);

  useEffect(() => {
    window.scrollTo(0, 0);

    fetch('http://localhost:8080/api/news')
      .then(r => r.json())
      .then(list => {
        const found = list.find(x => String(x.id) === id);
        setArticle(found ?? null);
      });
  }, [id]);

  if (!article)
    return <div className="container fade-news-page">Caricamento...</div>;

  return (
    <div className="container fade-news-page">

      {/* HERO PARALLAX */}
      <div className="article-hero-wrapper">
        <img
          src={'http://localhost:8080/api/images/' + article.thumbnail}
          alt={article.title}
          className="article-hero"
        />
      </div>

      <h2 className="article-title">{article.title}</h2>

      <div className="article-content animate-article">
        {article.content.repeat(8)}
      </div>

    </div>
  );
}
