import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/news.css";

export default function News() {
  const navigate = useNavigate();
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    let mounted = true;

    fetch("http://localhost:8080/api/news")
      .then((r) => r.json())
      .then((data) => mounted && setArticles(data || []))
      .catch((e) => {
        console.error("Errore news:", e);
        mounted && setArticles([]);
      })
      .finally(() => mounted && setLoading(false));

    return () => (mounted = false);
  }, []);

  return (
    <div className="container">
      <h2 className="section-title">News</h2>

      {loading ? (
        <div className="loading">Caricamento...</div>
      ) : (
        <div className="news-list">
          {articles.length === 0 ? (
            <div className="loading">Nessuna news disponibile.</div>
          ) : (
            articles.map((a, i) => {
              const img = `http://localhost:8080/api/images/${a.thumbnail}`;

              return (
                <div
                  key={a.id}
                  className="news-item netflix-item"
                  style={{ animationDelay: `${i * 0.12}s` }}
                  onClick={() => navigate(`/news/${a.id}`)}
                >
                  {/* Thumbnail */}
                  <img
                    className="news-thumb"
                    src={img}
                    alt={a.title}
                    onError={(e) => {
                      e.currentTarget.onerror = null;
                      e.currentTarget.src = "/fallback-news.jpg";
                    }}
                  />

                  {/* Testo */}
                  <div className="news-content">
                    <div className="news-category">MOVIE NEWS</div>

                    <h3 className="news-title">{a.title}</h3>

                    <p className="news-preview">
                      {a.content?.length > 200
                        ? a.content.substring(0, 200) + "..."
                        : a.content}
                    </p>
                  </div>

                </div>
              );
            })
          )}
        </div>
      )}
    </div>
  );
}
