import React, { useEffect, useState, useRef } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/news.css';

export default function NewsArticle() {
  const { id } = useParams();
  const [article, setArticle] = useState(null);

  const heroRef = useRef(null);

  // PARALLAX SCROLL EFFECT
  useEffect(() => {
    function handleScroll() {
      if (!heroRef.current) return;
      const offset = window.scrollY * 0.35; // intensità parallax
      heroRef.current.style.transform = `translateY(${offset}px) scale(1.1)`;
    }

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  useEffect(() => {
    fetch('http://localhost:8080/api/news')
      .then(r => r.json())
      .then(list => {
        const found = list.find(x => String(x.id) === id);
        setArticle(found || {
          id,
          title: "Articolo",
          thumbnail: "news1_thumbnail.jpg",
          content: "Contenuto demo..."
        });
      });
  }, [id]);

  if (!article)
    return <div className="container">Caricamento...</div>;

  return (
    <div className="container news-article animate-article">

      {/* HERO IMAGE PARALLAX */}
      <div className="article-hero-wrapper">
        <img
          ref={heroRef}
          className="article-hero parallax-img"
          src={'http://localhost:8080/api/images/' + article.thumbnail}
          alt={article.title}
        />
      </div>

      <h1 className="article-title">{article.title}</h1>

      <div className="article-content">
        {article.content.repeat(10)}
      </div>

    </div>
  );
}
