import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "../styles/news.css";

export default function NewsArticle() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [article, setArticle] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    let mounted = true;

    fetch("http://localhost:8080/api/news/" + id)
      .then((r) => r.json())
      .then((data) => mounted && setArticle(data))
      .catch(() => mounted && setArticle(null))
      .finally(() => mounted && setLoading(false));

    return () => (mounted = false);
  }, [id]);

  if (loading) return <div className="loading">Caricamento...</div>;
  if (!article) return <div className="loading">Articolo non trovato.</div>;

  const img = `http://localhost:8080/api/images/${article.thumbnail}`;

  return (
    <div className="container news-article">
      {/* HERO */}
      <div className="news-hero" style={{ "--bg": `url(${img})` }}>
        <div className="news-hero-inner">
          <img
            className="news-hero-thumb"
            src={img}
            alt={article.title}
            onError={(e) => {
              e.currentTarget.onerror = null;
              e.currentTarget.src = "/fallback-news.jpg";
            }}
          />

          <div className="news-hero-info">
            <div className="news-category">MOVIE NEWS</div>
            <h1 className="news-hero-title">{article.title}</h1>
          </div>
        </div>
      </div>

      {/* TESTO */}
      <div className="news-full-content">
        {article.content?.split("\n").map((p, i) => (
          <p key={i} className="news-paragraph">
            {p.trim()}
          </p>
        ))}

        <button className="navbtn" onClick={() => navigate(-1)}>
          ‚Üê Torna indietro
        </button>
      </div>
    </div>
  );
}
