import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "../styles/newsArticle.css";

export default function NewsArticle() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [article, setArticle] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:8080/api/news/${id}`)
      .then((r) => r.json())
      .then((data) => setArticle(data))
      .catch((e) => console.error("Errore caricamento articolo:", e));
  }, [id]);

  if (!article) return <div className="loading">Caricamento...</div>;

  const img = `http://localhost:8080/api/images/${article.thumbnail}`;

  return (
    <div className="newsarticle-container">

      {/* BACK BUTTON */}
      <button className="back-btn" onClick={() => navigate("/news")}>
        ‚Üê Indietro
      </button>

      {/* HERO */}
      <div
        className="newsarticle-hero"
        style={{ backgroundImage: `url('${img}')` }}
      >
        <div className="newsarticle-hero-overlay" />
      </div>

      {/* MAIN CONTENT */}
      <div className="newsarticle-content-wrapper">

        <img src={img} alt={article.title} className="newsarticle-image" />

        <div className="newsarticle-text">

          <div className="category">MOVIE NEWS</div>

          <h1 className="article-title">{article.title}</h1>

          <p className="article-content">{article.content}</p>

        </div>

      </div>
    </div>
  );
}
