import React, { useEffect, useState } from 'react';
import '../styles/news.css';
import { useNavigate } from 'react-router-dom';

export default function News() {
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    async function load() {
      try {
        const res = await fetch('http://localhost:8080/api/news');
        const data = await res.json();
        setArticles(data);
      } catch (error) {
        console.error("Errore nel caricamento news:", error);
      }
      setLoading(false);
    }
    load();
  }, []);

  if (loading) {
    return (
      <div className="container fade-news-page">
        <h2 className="section-title">News</h2>
        <div className="loading">Caricamento...</div>
      </div>
    );
  }

  return (
    <div className="container fade-news-page">
      <h2 className="section-title">News</h2>

      <div>
        {articles.map(a => (
          <div
            className="news-item fade-news-item"
            key={a.id}
            onClick={() => navigate('/news/' + a.id)}
          >
            <img
              src={'http://localhost:8080/api/images/' + a.thumbnail}
              alt={a.title}
            />

            <div>
              <div className="news-label">News</div>
              <div className="news-title">{a.title}</div>
              <div className="news-excerpt">
                {(a.content || '').substring(0, 160)}...
              </div>
            </div>

          </div>
        ))}
      </div>
    </div>
  );
}
