import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/news.css";

const News = () => {
  const navigate = useNavigate();
  const [articles, setArticles] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8080/api/news")
      .then((r) => r.json())
      .then(setArticles);
  }, []);

  return (
    <div className="content fade-news-page">
      <h1 className="section-title">News</h1>

      <div className="news-grid">
        {articles.map((n) => (
          <div
            key={n.id}
            className="news-card fade-news-item"
            onClick={() => navigate(`/news/${n.id}`)}
          >
            <img
              src={`http://localhost:8080/api/images/${n.thumbnail}`}
              alt="news"
              className="news-card-img"
            />

            <div className="news-card-body">
              <p className="news-label">News</p>
              <h2 className="news-card-title">{n.title}</h2>
              <p className="news-card-text">
                {n.content.substring(0, 110)}...
              </p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default News;
