import React, { useEffect, useState, useRef } from 'react';
import { useParams } from 'react-router-dom';
import '../styles/news.css';

export default function NewsArticle() {
  const { id } = useParams();
  const [article, setArticle] = useState(null);
  const heroRef = useRef(null);

  // Carica l’articolo completo dal backend
  useEffect(() => {
    fetch('http://localhost:8080/api/news/' + id)
      .then(r => r.json())
      .then(setArticle);
  }, [id]);

  // Effetto parallax sull’immagine
  useEffect(() => {
    function onScroll() {
      if (!heroRef.current) return;
      const offset = window.scrollY * 0.20;
      heroRef.current.style.transform = `translateY(${offset}px) scale(1.05)`;
    }
    window.addEventListener('scroll', onScroll);
    return () => window.removeEventListener('scroll', onScroll);
  }, []);

  if (!article) return <div className="container">Caricamento...</div>;

  return (
    <div className="container animate-article news-article">
      <h2 className="section-title">News</h2>

      <img
        ref={heroRef}
        className="article-hero"
        src={'http://localhost:8080/api/images/' + article.thumbnail}
        alt={article.title}
      />

      <h1 className="article-title">{article.title}</h1>

      <div className="article-content">
        {article.content.repeat(10)}
      </div>
    </div>
  );
}
