import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import "../styles/NewsArticle.css";

export default function NewsArticle() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [article, setArticle] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:8080/api/news/${id}`)
      .then((res) => res.json())
      .then((data) => setArticle(data))
      .catch((err) => console.error(err));
  }, [id]);

  // Scroll reveal (tieni, funziona bene)
  useEffect(() => {
    const elems = document.querySelectorAll(".reveal");
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.isIntersecting) entry.target.classList.add("visible");
        });
      },
      { threshold: 0.2 }
    );

    elems.forEach((el) => observer.observe(el));
  }, []);

  if (!article) return <p className="loading">Caricamento...</p>;

  const imageUrl = `http://localhost:8080/images/${article.cover}`;

  const paragraphs = article.content
    ? article.content.split(/\n+/).filter((p) => p.trim() !== "")
    : [];

  return (
    <div className="article-page">

      {/* HERO */}
      <div className="hero" style={{ backgroundImage: `url(${imageUrl})` }}>
        <div className="hero-blur"></div>
        <div className="hero-overlay"></div>

        <button className="back-btn" onClick={() => navigate(-1)}>
          ‚Üê Torna indietro
        </button>
      </div>

      {/* CONTENUTO SCORREVOLE */}
      <div className="article-content-container">
        <div className="article-content-scroll">

          <h1 className="article-title reveal">{article.title}</h1>

          {paragraphs.length > 0 ? (
            paragraphs.map((p, i) => (
              <p key={i} className="article-paragraph reveal" style={{ "--d": `${i * 0.12}s` }}>
                {p}
              </p>
            ))
          ) : (
            <p className="article-paragraph reveal">{article.content}</p>
          )}

          <div className="article-date reveal" style={{ "--d": "0.6s" }}>
            Pubblicato: {article.date}
          </div>

        </div>
      </div>

    </div>
  );
}
