import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import "../styles/NewsArticle.css";

export default function NewsArticle() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [article, setArticle] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:8080/api/news/${id}`)
      .then((res) => res.json())
      .then((data) => setArticle(data))
      .catch((err) => console.error(err));
  }, [id]);

  if (!article) return <p className="loading">Caricamento...</p>;

  const imageUrl = `http://localhost:8080/images/${article.thumbnail}`;

  return (
    <div className="article-classic-page">

      <button className="classic-back-btn" onClick={() => navigate(-1)}>
        ‚Üê Torna indietro
      </button>

      <div className="classic-header">
        <img src={imageUrl} alt={article.title} className="classic-thumbnail" />

        <h1 className="classic-title">{article.title}</h1>
        <p className="classic-date">Pubblicato: {article.date}</p>
      </div>

      <div className="classic-content">
        {article.content.split("\n").map((p, i) => (
          <p key={i}>{p}</p>
        ))}
      </div>

    </div>
  );
}
