import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/news.css";

export default function News() {
  const navigate = useNavigate();
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    let mounted = true;
    fetch("http://localhost:8080/api/news")
      .then((r) => r.json())
      .then((data) => {
        if (!mounted) return;
        setArticles(data);
      })
      .catch((e) => {
        console.error("Errore caricamento news:", e);
      })
      .finally(() => {
        if (mounted) setLoading(false);
      });
    return () => (mounted = false);
  }, []);

  if (loading) {
    return (
      <div className="news-page">
        <h1 className="news-title">News</h1>
        <div className="news-list">
          <div className="news-card placeholder" />
          <div className="news-card placeholder" />
        </div>
      </div>
    );
  }

  return (
    <div className="news-page">
      <h1 className="news-title">News</h1>

      <div className="news-list">
        {articles.map((n) => {
          const img = `http://localhost:8080/api/images/${n.thumbnail}`;
          return (
            <div
              key={n.id}
              className="news-card"
              onClick={() => navigate(`/news/${n.id}`)}
              role="button"
              tabIndex={0}
              onKeyDown={(e) => {
                if (e.key === "Enter") navigate(`/news/${n.id}`);
              }}
            >
              <img
                className="news-card-img"
                src={img}
                alt={n.title}
                loading="lazy"
                onError={(ev) => {
                  ev.currentTarget.onerror = null;
                  ev.currentTarget.src = "/fallback-news.jpg";
                }}
              />

              <div className="news-card-content">
                <div className="news-category">News</div>
                <div className="news-card-title">{n.title}</div>
                <div className="news-card-description">
                  {n.content?.length > 160
                    ? n.content.substring(0, 160) + "..."
                    : n.content}
                </div>
              </div>
            </div>
          );
        })}
      </div>
    </div>
  );
}
