import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "../styles/news.css";

export default function NewsArticle() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [article, setArticle] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    let mounted = true;
    fetch("http://localhost:8080/api/news/" + id)
      .then((r) => r.json())
      .then((data) => {
        if (!mounted) return;
        setArticle(data);
      })
      .catch((e) => {
        console.error("Errore caricamento articolo:", e);
        if (!mounted) setArticle(null);
      })
      .finally(() => {
        if (mounted) setLoading(false);
      });
    return () => (mounted = false);
  }, [id]);

  if (loading) {
    return <div className="container">Caricamento articolo...</div>;
  }
  if (!article) {
    return <div className="container">Articolo non trovato.</div>;
  }

  const imageUrl = `http://localhost:8080/api/images/${article.thumbnail}`;

  return (
    <div className="container film-page">
      {/* HERO stile FilmDetail: sfondo sfumato (via --bg) */}
      <div className="film-hero" style={{ "--bg": `url(${imageUrl})` }}>
        <div className="film-hero-content">
          {/* poster piccolo a sinistra (stessa immagine thumbnail) */}
          <img
            className="film-poster"
            src={imageUrl}
            alt="poster"
            onError={(e) => {
              e.currentTarget.onerror = null;
              e.currentTarget.src = "/fallback-news.jpg";
            }}
          />

          {/* info principali a destra */}
          <div className="film-info">
            <h1 className="film-title">{article.title}</h1>
            <div className="film-meta">News</div>
          </div>
        </div>
      </div>

      {/* riquadro contenuto (stesso stile film-detail) */}
      <div className="film-detail">
        {/* manteniamo il testo formattato a paragrafi */}
        {article.content &&
          article.content.split("\n").map((p, i) => (
            <p key={i} className="film-plot">
              {p.trim()}
            </p>
          ))}

        <div style={{ marginTop: 18 }}>
          <button
            className="navbtn"
            onClick={() => navigate(-1)}
            style={{ padding: "8px 12px" }}
          >
            ‚Üê Torna indietro
          </button>
        </div>
      </div>
    </div>
  );
}
