import React, {useEffect, useState} from 'react';
import '../styles/home.css';
import { useNavigate } from 'react-router-dom';

/**
 * Home page - shows genre tabs and 3 thumbnails per selected genre.
 * Clicking a thumbnail navigates to /film/:id (film detail page).
 */
export default function Home(){
  const [data,setData] = useState({drammatico:[],sentimentale:[]});
  const [selected, setSelected] = useState('drammatico');
  const navigate = useNavigate();

  useEffect(()=>{
    // load films grouped by genre
    fetch('http://localhost:8080/api/genres').then(r=>r.json()).then(setData);
  },[]);

  const films = data[selected] || [];

  return (<div className='container'>
    <h2 className='section-title'>Genere</h2>
    <div className='genre-tabs'>
      <div className={'tab '+(selected==='drammatico'?'active':'')} onClick={()=>setSelected('drammatico')}>Drammatico</div>
      <div className={'tab '+(selected==='sentimentale'?'active':'')} onClick={()=>setSelected('sentimentale')}>Sentimentale</div>
    </div>

    <div className='card-grid'>
      {films.slice(0,3).map(f => (
        <div key={f.id}>
          <img className='thumb' alt={f.title} src={'http://localhost:8080/api/images/'+f.thumbnail}
               onClick={()=> navigate('/film/'+f.id)} />
          <div style={{textAlign:'center',marginTop:6}}>{f.title} ({f.year})</div>
        </div>
      ))}
    </div>
  </div>);
}
