import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import "../styles/newsArticle.css";

export default function NewsArticle() {
  const { id } = useParams();
  const navigate = useNavigate();

  const [article, setArticle] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:8080/api/news/${id}`)
      .then(r => r.json())
      .then(setArticle)
      .catch(() => setArticle(null));
  }, [id]);

  if (!article) return <div style={{ padding: 40, color: "#fff" }}>Caricamento…</div>;

  const heroImg = `http://localhost:8080/api/images/${article.thumbnail}`;

  return (
    <div className="news-article">

      {/* HERO IDENTICO ALLA PAGINA FILM */}
      <div
        className="article-hero"
        style={{ backgroundImage: `url(${heroImg})` }}
      >
        <div className="hero-overlay">

          <button className="back-btn" onClick={() => navigate(-1)}>
            ⟵ Torna indietro
          </button>

          <h1 className="article-title">{article.title}</h1>
          <div className="article-category">News</div>
        </div>
      </div>

      {/* CONTENUTO */}
      <div className="article-content">
        {article.content.split("\n").map((p, i) => (
          <p key={i}>{p}</p>
        ))}
      </div>

    </div>
  );
}
