import React, {useEffect, useState} from 'react';
import '../styles/film.css';
import { useParams } from 'react-router-dom';

/**
 * Film detail page showing title, year, country, director and a wide screenshot.
 */
export default function FilmDetail(){
  const { id } = useParams();
  const [film, setFilm] = useState(null);

  useEffect(()=>{
    fetch('http://localhost:8080/api/films/'+id).then(r=>r.json()).then(setFilm);
  },[id]);

  if(!film) return <div className='container'>Caricamento...</div>;

  return (<div className='container'>
    <div className='film-detail'>
      <h2 className='film-title'>{film.title}</h2>
      <div className='film-meta'>{film.year} • {film.country} • Regia: {film.director}</div>
      <img className='screenshot-wide' src={'http://localhost:8080/api/images/'+film.screenshot} alt='screenshot' />
      <p className='film-plot'>{film.plot}</p>
    </div>
  </div>);
}
