import React from "react";
import { useNavigate, useLocation } from "react-router-dom";
import "../style/news.css"; // importa il CSS Netflix-style

export default function NewsArticle() {
  const location = useLocation();
  const navigate = useNavigate();

  // Dati passati dal click sulla news (come giÃ  fai per i film)
  const article = location.state?.article;

  if (!article) {
    return (
      <div className="news-detail">
        <p className="news-paragraph">
          Errore: articolo non trovato.
        </p>
        <button className="news-back-btn" onClick={() => navigate(-1)}>
          Torna Indietro
        </button>
      </div>
    );
  }

  return (
    <div className="news-page netflix-enter">

      {/* ======================
             HERO
         ====================== */}
      <div
        className="news-hero"
        style={{ "--bg": `url(${article.image})` }}
      >
        <div className="news-hero-content">

          {/* Poster */}
          <img
            className="news-poster"
            src={article.image}
            alt={article.title}
          />

          {/* Info */}
          <div className="news-info">
            <h1 className="news-title">{article.title}</h1>

            {article.date && (
              <div className="news-meta">ðŸ—“ {article.date}</div>
            )}

            {article.category && (
              <div className="news-meta">ðŸ“Œ {article.category}</div>
            )}
          </div>

        </div>
      </div>

      {/* ======================
           TESTO ARTICOLO
         ====================== */}
      <div className="news-detail">
        {article.text
          .split("\n")
          .filter((p) => p.trim() !== "")
          .map((p, i) => (
            <p key={i} className="news-paragraph">
              {p}
            </p>
          ))}

        <button className="news-back-btn" onClick={() => navigate(-1)}>
          Torna Indietro
        </button>
      </div>
    </div>
  );
}
