import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import "../styles/news.css";

const NewsArticle = () => {
  const { id } = useParams();
  const [article, setArticle] = useState(null);

  useEffect(() => {
    fetch("http://localhost:8080/api/news/" + id)
      .then((r) => r.json())
      .then(setArticle);
  }, [id]);

  if (!article) return <p>Caricamento articolo...</p>;

  const imageUrl = `http://localhost:8080/api/images/${article.thumbnail}`;

  return (
    <div className="news-article animate-article">
      {/* SFONDO SFUOCATO */}
      <div
        className="article-bg-blur"
        style={{ backgroundImage: `url(${imageUrl})` }}
      ></div>

      {/* IMMAGINE HERO */}
      <img src={imageUrl} alt={article.title} className="article-hero" />

      <h1 className="article-title">{article.title}</h1>

      <div className="article-content">
        {article.content.split("\n").map((p, i) => (
          <p key={i}>{p.trim()}</p>
        ))}
      </div>
    </div>
  );
};

export default NewsArticle;
