import React from "react";
import { useNavigate } from "react-router-dom";
import "../styles/news.css";

const News = () => {
  const navigate = useNavigate();

  const articles = [
    {
      id: 1,
      title: "Intervista esclusiva a Sofia Moretti: «Questo ruolo mi ha cambiata»",
      excerpt:
        "L’attrice italiana Sofia Moretti, protagonista del nuovo film *Oltre il Silenzio*, racconta come il ruolo di una ricercatrice affetta da un disturbo neurologico l’abbia messa alla prova come mai prima...",
      image: "/img/news/news1.jpg",
    },
    {
      id: 2,
      title: "Documentario *Senza Confini* emoziona pubblico e critica",
      excerpt:
        "Il documentario *Senza Confini* racconta la storia di tre volontari impegnati nelle zone più fragili del mondo...",
      image: "/img/news/news2.jpg",
    },
    {
      id: 3,
      title: "Roma ospita la premiere europea di *Falling Skies*",
      excerpt:
        "Il red carpet romano ha visto sfilare l’intero cast del nuovo film fantasy *Falling Skies*...",
      image: "/img/news/news3.jpg",
    },
  ];

  return (
    <div className="content fade-news-page">
      <h1 className="section-title">News</h1>

      <div className="news-grid">
        {articles.map((n) => (
          <div
            key={n.id}
            className="news-card fade-news-item"
            onClick={() => navigate(`/news/${n.id}`)}
          >
            <img src={n.image} alt="news" className="news-card-img" />

            <div className="news-card-body">
              <p className="news-label">News</p>
              <h2 className="news-card-title">{n.title}</h2>
              <p className="news-card-text">{n.excerpt}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default News;
