import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/news.css";

export default function News() {
  const navigate = useNavigate();
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    let mounted = true;

    fetch("http://localhost:8080/api/news")
      .then((r) => r.json())
      .then((data) => mounted && setArticles(Array.isArray(data) ? data : []))
      .catch((e) => {
        console.error("Errore news:", e);
        mounted && setArticles([]);
      })
      .finally(() => mounted && setLoading(false));

    return () => (mounted = false);
  }, []);

  return (
    <div className="container">
      <h2 className="section-title">News</h2>

      {loading ? (
        <div className="loading">Caricamento...</div>
      ) : articles.length === 0 ? (
        <div className="loading">Nessuna news disponibile.</div>
      ) : (
        <div className="news-list netflix-enter">
          {articles.map((a, i) => {
            const img = `http://localhost:8080/api/images/${a.thumbnail}`;
            return (
              <article
                key={a.id}
                className="news-item netflix-item"
                style={{ animationDelay: `${i * 0.09}s` }}
              >
                <button
                  className="news-thumb-wrap"
                  onClick={() => navigate(`/news/${a.id}`)}
                  aria-label={`Apri articolo ${a.title}`}
                >
                  <img
                    className="news-thumb"
                    src={img}
                    alt={a.title}
                    onError={(e) => {
                      e.currentTarget.onerror = null;
                      e.currentTarget.src = "/fallback-news.jpg";
                    }}
                  />
                </button>

                <div className="news-content">
                  <div className="news-meta">MOVIE NEWS</div>

                  <h3
                    className="news-title"
                    onClick={() => navigate(`/news/${a.id}`)}
                  >
                    {a.title}
                  </h3>

                  <p className="news-preview">
                    {a.content
                      ? a.content.length > 220
                        ? a.content.substring(0, 220) + "..."
                        : a.content
                      : ""}
                  </p>

                  <div className="news-actions">
                    <button
                      className="ghost-btn"
                      onClick={() => navigate(`/news/${a.id}`)}
                    >
                      Leggi
                    </button>
                  </div>
                </div>
              </article>
            );
          })}
        </div>
      )}
    </div>
  );
}
