import React, {useEffect, useState} from 'react';
import { useParams } from 'react-router-dom';
import '../styles/news.css';

/**
 * Full news article page. For demo, fetches the news pool and finds the item by id.
 */
export default function NewsArticle(){
  const { id } = useParams();
  const [article, setArticle] = useState(null);

  useEffect(()=>{
    fetch('http://localhost:8080/api/news').then(r=>r.json()).then(list=>{
      const found = list.find(x=>String(x.id)===id);
      if(found) setArticle(found);
      else {
        // If not in current 3-random, create a placeholder by id for demo
        setArticle({id:id, title:'Articolo', thumbnail:'news1_thumbnail.jpg', content:'Contenuto articolo esteso... (testo aggiuntivo).'});
      }
    });
  },[id]);

  if(!article) return <div className='container'>Caricamento...</div>;

  return (<div className='container'>
    <h2 className='section-title'>News</h2>
    <div style={{marginTop:18, background:'rgba(255,255,255,0.02)',padding:16,borderRadius:8}}>
      <h2 style={{color:'#fbe2a6'}}>{article.title}</h2>
      <img style={{width:'100%',maxWidth:800,display:'block',margin:'8px auto',borderRadius:8}} src={'http://localhost:8080/api/images/'+article.thumbnail} alt='news' />
      <p style={{lineHeight:1.6,color:'#dbeaf8'}}>{article.content.repeat(12)}</p>
    </div>
  </div>);
}
