import React, { useEffect, useState } from "react";
import "../styles/home.css";

import logo from "../assets/logo.png";
import clapBase from "../assets/clap_base.png";
import clapTop from "../assets/clap_top.png";

function Home() {
  const [showSplash, setShowSplash] = useState(true);

  useEffect(() => {
    const timer = setTimeout(() => {
      setShowSplash(false);
    }, 3600); // durata totale splash

    return () => clearTimeout(timer);
  }, []);

  return (
    <>
      {showSplash && (
        <div className="splash-overlay">
          <div className="splash-content">
            <img src={logo} alt="Cinema Web App" className="splash-logo" />

            <div className="clap-wrapper">
              <img src={clapBase} alt="Clap base" className="clap-base" />
              <img src={clapTop} alt="Clap top" className="clap-top" />
            </div>
          </div>
        </div>
      )}

      {/* HOME PAGE */}
      <section className="home">
        <h2 className="section-title">Genere</h2>

        <div className="genre-tabs">
          <button className="tab active">Drammatico</button>
          <button className="tab">Sentimentale</button>
        </div>

        <div className="card-grid">
          <div className="film-card">
            <img
              src="https://image.tmdb.org/t/p/w500/rPdtLWNsZmAtoZl9PK7S2wE3qiS.jpg"
              alt="The Godfather"
              className="thumb"
            />
            <div className="card-title">The Godfather (1972)</div>
          </div>

          <div className="film-card">
            <img
              src="https://image.tmdb.org/t/p/w500/sF1U4EUQS8YHUYjNl3pMGNIQyr0.jpg"
              alt="Schindler's List"
              className="thumb"
            />
            <div className="card-title">Schindler's List (1993)</div>
          </div>

          <div className="film-card">
            <img
              src="https://image.tmdb.org/t/p/w500/gXg2QYq1PjI5v0S1jZP6p6e5LxW.jpg"
              alt="Atonement"
              className="thumb"
            />
            <div className="card-title">Atonement (2007)</div>
          </div>
        </div>
      </section>
    </>
  );
}

export default Home;
