import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/news.css";

export default function News() {
  const navigate = useNavigate();
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    let mounted = true;
    fetch("http://localhost:8080/api/news")
      .then((r) => r.json())
      .then((data) => {
        if (!mounted) return;
        setArticles(Array.isArray(data) ? data : []);
      })
      .catch((e) => {
        console.error("Errore caricamento news:", e);
        if (!mounted) return;
        setArticles([]);
      })
      .finally(() => {
        if (mounted) setLoading(false);
      });
    return () => (mounted = false);
  }, []);

  return (
    <div className="container news-page">
      <h2 className="section-title">News</h2>

      {loading ? (
        <div style={{ color: "#cbd9ea", textAlign: "center", marginTop: 28 }}>
          Caricamento...
        </div>
      ) : articles.length === 0 ? (
        <div style={{ color: "#cbd9ea", textAlign: "center", marginTop: 28 }}>
          Nessuna news disponibile.
        </div>
      ) : (
        <div className="news-list">
          {articles.map((a, i) => {
            const img = `http://localhost:8080/api/images/${a.thumbnail}`;
            return (
              <article
                key={a.id}
                className="news-item netflix-item"
                style={{ animationDelay: `${i * 0.08}s` }}
                onClick={() => navigate(`/news/${a.id}`)}
              >
                <div className="news-thumb-wrap">
                  <img
                    className="thumb news-thumb"
                    src={img}
                    alt={a.title}
                    onError={(e) => {
                      e.currentTarget.onerror = null;
                      e.currentTarget.src = "/fallback-news.jpg";
                    }}
                  />
                </div>

                <div className="news-body">
                  <div className="news-category">Movie News</div>
                  <h3 className="news-headline">{a.title}</h3>
                  <p className="news-excerpt">
                    {a.content
                      ? a.content.length > 220
                        ? a.content.substring(0, 220) + "..."
                        : a.content
                      : ""}
                  </p>
                </div>
              </article>
            );
          })}
        </div>
      )}
    </div>
  );
}
