import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";
import "./NewsArticle.css";

export default function NewsArticle() {
  const { id } = useParams();
  const [news, setNews] = useState(null);

  useEffect(() => {
    fetch(`http://localhost:3001/news/${id}`)
      .then(res => res.json())
      .then(data => setNews(data));
  }, [id]);

  if (!news) return <div className="news-article-page">Caricamento...</div>;

  return (
    <div className="news-article-page">

      {/* Hero */}
      <div 
        className="news-article-hero"
        style={{
          backgroundImage: `url(${news.thumbnail})`
        }}
      >
        <div className="hero-overlay"></div>

        <div className="hero-content">
          <img 
            src={news.thumbnail} 
            alt="" 
            className="news-article-hero-img"
          />

          <h1 className="news-article-title">
            {news.title}
          </h1>

          <div className="news-article-meta">
            <span>{news.category?.toUpperCase()}</span> •{" "}
            <span>{news.date}</span>
          </div>
        </div>
      </div>

      {/* Body */}
      <div className="news-article-body">
        <Link to="/news" className="news-article-back">← Torna alle News</Link>

        <div className="news-article-content">
          <p>{news.content}</p>
        </div>
      </div>

    </div>
  );
}
