import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import "../styles/newsArticle.css";

/*
  NewsArticle.js
  - recupera l'articolo da /api/news/:id
  - prova diversi percorsi immagine (fallbacks)
  - effettua preload dell'immagine e mostra placeholder se non raggiungibile
  - stampa log utili in console per debug
*/

const PLACEHOLDER = "/images/placeholder_news.jpg"; // crea o aggiungi un placeholder in backend/static/images

function tryImageLoad(url) {
  // ritorna Promise che risolve true se raggiungibile (status 200), false altrimenti
  return new Promise((resolve) => {
    const img = new Image();
    img.onload = () => resolve(true);
    img.onerror = () => resolve(false);
    img.src = url;
  });
}

export default function NewsArticle() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [article, setArticle] = useState(null);
  const [imageSrc, setImageSrc] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setLoading(true);
    // fetch articolo
    fetch(`http://localhost:8080/api/news/${id}`)
      .then((res) => {
        if (!res.ok) throw new Error(`API error ${res.status}`);
        return res.json();
      })
      .then(async (data) => {
        setArticle(data);

        // costruzione possibili url immagine:
        // 1) se backend già fornisce article.imageUrl (assoluto/relativo) prova quello
        // 2) /images/<article.thumbnail> (nome esatto se presente)
        // 3) varianti: rimuovi "_l", aggiungi/ rimuovi estensione jpg, prova _thumbnail
        // 4) se tutto fallisce uso PLACEHOLDER
        const tries = [];

        // se backend fornisce campo imageUrl (pieno o relativo)
        if (data.imageUrl) {
          tries.push(data.imageUrl);
          // se è solo nome file senza /images/ lo mettiamo dentro
          if (!data.imageUrl.startsWith("/images") && !data.imageUrl.startsWith("http")) {
            tries.push(`/images/${data.imageUrl}`);
          }
        }

        // se esiste field thumbnail
        if (data.thumbnail) {
          // variants:
          const raw = data.thumbnail;
          tries.push(`/images/${raw}`);
          // if has _l suffix or missing suffix try both
          if (raw.endsWith("_l.jpg") || raw.endsWith("_l.png")) {
            const removed = raw.replace(/_l(\.jpg|\.png)$/i, "$1");
            tries.push(`/images/${removed}`);
          } else {
            // try adding _l
            const addLjpg = raw.replace(/(\.jpg|\.png)$/i, "_l$1");
            tries.push(`/images/${addLjpg}`);
          }
          // try also with _thumbnail variation
          if (!raw.includes("thumbnail")) {
            const thumbname = raw.replace(/(\.jpg|\.png)$/i, "_thumbnail$1");
            tries.push(`/images/${thumbname}`);
          }
        }

        // generic fallback attempt: news<ID>_thumbnail.jpg
        tries.push(`/images/news${id}_thumbnail.jpg`);
        tries.push(`/images/news${id}_thumbnail_l.jpg`);
        tries.push(`/images/news${id}.jpg`);
        tries.push(`/images/news${id}_thumb.jpg`);

        // dedup
        const candidates = [...new Set(tries)].filter(Boolean);

        console.groupCollapsed(`NewsArticle(${id}) - image probes`);
        console.log("Article from API:", data);
        console.log("Candidates:", candidates);
        console.groupEnd();

        // try them in order
        let found = null;
        for (const url of candidates) {
          // if url starts with http, use as is; otherwise prefix with host if needed (we assume static is on same host/port 8080)
          let testUrl = url;
          if (!/^https?:\/\//i.test(testUrl) && !testUrl.startsWith("/")) {
            testUrl = "/".concat(testUrl);
          }
          // normalize to full address on backend host (http://localhost:8080) if path begins with /images
          if (testUrl.startsWith("/images")) {
            testUrl = `http://localhost:8080${testUrl}`;
          }
          // log attempt
          console.log("Probing image URL:", testUrl);
          // await preload
          // eslint-disable-next-line no-await-in-loop
          const ok = await tryImageLoad(testUrl);
          if (ok) {
            found = testUrl;
            console.log("Image OK:", testUrl);
            break;
          } else {
            console.warn("Image not found:", testUrl);
          }
        }

        if (found) setImageSrc(found);
        else {
          console.error("No valid image found for article", id, ", using placeholder");
          setImageSrc(PLACEHOLDER);
        }

        setLoading(false);
      })
      .catch((err) => {
        console.error("Failed to load article:", err);
        setLoading(false);
      });
  }, [id]);

  if (loading) return <div className="article-container"><p>Loading articolo...</p></div>;
  if (!article) return <div className="article-container"><p>Articolo non trovato.</p></div>;

  return (
    <div className="article-container">
      <button className="back-button ghost-btn" onClick={() => navigate(-1)}>← Torna indietro</button>

      <h1 className="article-title">{article.title}</h1>

      {imageSrc ? (
        <div className="article-image-wrap">
          <img
            src={imageSrc}
            alt={article.title}
            className="article-image"
            onError={(e) => {
              console.error("Image onError for src:", e.target.src);
              e.target.src = PLACEHOLDER;
            }}
          />
        </div>
      ) : null}

      <div className="article-content" dangerouslySetInnerHTML={{ __html: article.content.replace(/\n/g, "<br/><br/>") }} />

      <p className="article-date">Pubblicato il: {article.date || "—"}</p>
    </div>
  );
}
