import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "../styles/news.css";

export default function NewsArticle() {
  const { id } = useParams();
  const navigate = useNavigate();
  const [article, setArticle] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    let mounted = true;

    fetch("http://localhost:8080/api/news/" + id)
      .then((r) => r.json())
      .then((data) => mounted && setArticle(data))
      .catch((e) => {
        console.error("Errore caricamento articolo:", e);
        mounted && setArticle(null);
      })
      .finally(() => mounted && setLoading(false));

    return () => (mounted = false);
  }, [id]);

  if (loading) return <div className="container">Caricamento...</div>;
  if (!article) return <div className="container">Articolo non trovato.</div>;

  const imageUrl = `http://localhost:8080/api/images/${article.thumbnail}`;

  return (
    <div className="news-article-page">

      {/* üü• HERO grande stile HollywoodReporter */}
      <div
        className="news-hero"
        style={{ backgroundImage: `url(${imageUrl})` }}
      >
        <div className="news-hero-overlay" />

        <div className="news-hero-content">
          <h1 className="news-hero-title">{article.title}</h1>
          <div className="news-hero-meta">MOVIE NEWS</div>
        </div>
      </div>

      {/* üü¶ Corpo articolo */}
      <div className="news-body-container">
        <div className="news-body">
          {article.content.split("\n").map((p, i) => (
            <p key={i}>{p.trim()}</p>
          ))}

          <button className="back-button" onClick={() => navigate(-1)}>
            ‚Üê Torna indietro
          </button>
        </div>
      </div>

    </div>
  );
}
