import React from "react";
import { useParams, Link } from "react-router-dom";
import "../styles/newsArticle.css";

export default function NewsArticle() {
    const { id } = useParams();
    const news = require("../data/news.json").find((n) => n.id === parseInt(id));

    if (!news) return <p>Articolo non trovato</p>;

    return (
        <div className="news-article-container">

            {/* HERO */}
            <div className="news-article-hero">
                <div className="hero-overlay"></div>
                <img
                    src={news.thumbnail}
                    alt={news.title}
                    className="hero-image"
                />
                <h1 className="hero-title">{news.title}</h1>
            </div>

            {/* BACK LINK */}
            <Link to="/news" className="news-back">‚Üê Torna alle News</Link>

            {/* META */}
            <div className="news-meta">
                <span className="category">{news.category.toUpperCase()}</span>
                <span className="date">{news.date}</span>
            </div>

            {/* CONTENT */}
            <div className="news-content">
                {news.content}
            </div>

        </div>
    );
}
