import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "../styles/news.css";

export default function News() {
  const navigate = useNavigate();
  const [articles, setArticles] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    let mounted = true;
    fetch("http://localhost:8080/api/news")
      .then((r) => r.json())
      .then((data) => {
        if (!mounted) return;
        setArticles(data || []);
      })
      .catch((e) => {
        console.error("Errore caricamento news:", e);
        if (!mounted) return;
        setArticles([]);
      })
      .finally(() => {
        if (mounted) setLoading(false);
      });
    return () => (mounted = false);
  }, []);

  return (
    <div className="container">
      <h2 className="section-title">News</h2>

      {loading ? (
        <div style={{ color: "#cbd9ea", textAlign: "center", marginTop: 20 }}>
          Caricamento...
        </div>
      ) : (
        <div className="card-grid netflix-enter">
          {articles.length === 0 ? (
            <div style={{ color: "#cbd9ea", marginTop: 20 }}>
              Nessuna news disponibile.
            </div>
          ) : (
            articles.map((a, i) => {
              const img = `http://localhost:8080/api/images/${a.thumbnail}`;
              return (
                <div
                  key={a.id}
                  className="netflix-item"
                  style={{ animationDelay: `${i * 0.12}s`, cursor: "pointer" }}
                  onClick={() => navigate(`/news/${a.id}`)}
                >
                  {/* usa la stessa classe thumb delle locandine per coerenza */}
                  <img
                    className="thumb"
                    src={img}
                    alt={a.title}
                    onError={(e) => {
                      e.currentTarget.onerror = null;
                      e.currentTarget.src = "/fallback-news.jpg";
                    }}
                  />
                  <div className="card-title">{a.title}</div>
                  <div style={{ textAlign: "center", color: "#cbd9ea", marginTop: 6 }}>
                    {/* estratto breve sotto il titolo */}
                    {a.content ? (a.content.length > 100 ? a.content.substring(0, 100) + "..." : a.content) : ""}
                  </div>
                </div>
              );
            })
          )}
        </div>
      )}
    </div>
  );
}
