import React from "react";
import { useParams } from "react-router-dom";
import "../styles/news.css";

const NewsArticle = () => {
  const { id } = useParams();

  const articleData = {
    1: {
      title: "Intervista esclusiva a Sofia Moretti: «Questo ruolo mi ha cambiata»",
      image: "/img/news/news1.jpg",
      content: `
      L’attrice italiana Sofia Moretti, protagonista del nuovo film *Oltre il Silenzio*, racconta come il ruolo di una ricercatrice affetta da un disturbo neurologico l’abbia messa alla prova come mai prima. «Ho dovuto studiare a lungo, incontrare pazienti reali, capire le loro paure e la loro forza», dichiara.

      Il film, diretto da Riccardo Balestri, è già in corsa per diversi premi internazionali.

      Questa esperienza, racconta l’attrice, le ha cambiato profondamente la percezione delle difficoltà della vita e del valore della resilienza umana.
      `,
    },

    2: {
      title: "Documentario *Senza Confini* emoziona pubblico e critica",
      image: "/img/news/news2.jpg",
      content: `
      Il documentario *Senza Confini* segue la vita di tre volontari impegnati in missioni umanitarie. Il pubblico ha definito l’opera «un pugno allo stomaco, ma anche un inno alla speranza».

      Girato in quattro continenti, il film mostra la potenza della solidarietà.
      `,
    },

    3: {
      title: "Roma ospita la premiere europea di *Falling Skies*",
      image: "/img/news/news3.jpg",
      content: `
      Migliaia di fan si sono radunati davanti al teatro di Via Veneto per assistere alla premiere europea del nuovo film fantasy *Falling Skies*.

      L’intero cast era presente e ha sfilato sul red carpet tra applausi e selfie.
      `,
    },
  };

  const article = articleData[id];

  if (!article) return <p>Articolo non trovato</p>;

  return (
    <div className="news-article animate-article">
      {/* IMMAGE BLUR BACKGROUND */}
      <div
        className="article-bg-blur"
        style={{ backgroundImage: `url(${article.image})` }}
      ></div>

      {/* HERO IMAGE */}
      <img src={article.image} alt={article.title} className="article-hero" />

      <h1 className="article-title">{article.title}</h1>

      <div className="article-content">
        {article.content.split("\n").map((p, i) => (
          <p key={i}>{p.trim()}</p>
        ))}
      </div>
    </div>
  );
};

export default NewsArticle;
